items circulating have status set correctly
circing_copies_set circs 0 Circulating Copies Not Set to Checked Out Count.Status SELECT COUNT(*), ccs.name FROM m_asset_copy_legacy acp JOIN config.copy_status ccs ON ccs.id = acp.status WHERE acp.x_migrate AND ccs.id != 1;
-- no conflicting user names or barcodes
incumbent_usrnames Incumbent Usrnames actors 0 Count SELECT COUNT(*) FROM actor.usr WHERE usrname IN (SELECT usrname FROM m_actor_usr_legacy WHERE x_migrate);
incumbent_barcodes Incumbent Barcodes actors 0 Count SELECT COUNT(*) FROM actor.card WHERE barcode IN (SELECT barcode FROM m_actor_card WHERE usr IN (SELECT id FROM m_actor_usr_legacy WHERE x_migrate));
